import { Button
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Component} from "react/cjs/react.production.min";
import React from 'react'
import './course.css';
import {
  Card, CardText, CardBody, CardHeader, CardFooter, Col
} from 'reactstrap';
import Info from "./Info";

class Course extends Component {
    constructor(props) {
        super(props);

    this.state = {
        course: props.name,
        desc: props.desc,
        buttonText: "Add",
        details: props.details,
        isDisplay: props.display,
    }
    }
    handleButton = () => {
        if (!this.state.isDisplay) {
            this.setState({isDisplay: true})
            this.setState({buttonText: "Remove"});
            document.getElementById(this.state.course).style.borderColor = "green";
        } else {
            this.setState({isDisplay: false})
            this.setState({buttonText: "Add"});
            document.getElementById(this.state.course).style.borderColor = "gainsboro";
        }
        this.props.handleUpdate(this)
    }
    render() {
        return (
            <Col>
                <Card className="text-center" id={this.state.course}>
                    <CardHeader className="text-center"> {this.state.course}
                        <Info course={this.state.course} details={this.state.details}/>
                    </CardHeader>
                    <CardBody className="text-center">
                        <CardText>{this.state.desc}</CardText>
                    </CardBody>
                    <CardFooter className="text-center">
                        <Button color="primary" onClick={this.handleButton }>{this.state.buttonText}</Button>
                    </CardFooter>
                </Card>
            </Col>
        );
    }
}

export default Course;
