import 'bootstrap/dist/css/bootstrap.min.css';
import {Component} from "react/cjs/react.production.min";
import React from 'react'
import {
  Card, CardHeader, CardBody, Table, Row
} from 'reactstrap';
import Course from "./Course";

class AllCourses extends Component {
    constructor() {
        super()

        this.state = {
            courses: [
                {name: 'swen-250', desc: 'personal software eng',
                    details: 'C and fun with vi and command line', display: false},
                {name: 'swen-331', desc: 'secure software',
                    details: 'Fuzzer - you will love it, you will fear it!', display: false},
                {name: 'swen-440', desc: 'system architecture', display: false,
                    details: "Services?  What what's a service?  I need to do math for metrics?"},
                {name: 'swen-344', desc: 'web engineering', display: false,
                    details: "You mean web pages, right?  Wait, there's more?"}
            ]
        }
        this.handleUpdate = this.handleUpdate.bind(this)
    }
    handleUpdate = (course) => {
        for(let i= 0; i < this.state.courses.length; i++) {
            if (this.state.courses[i].name === course.props.name) {
                let temp = this.state.courses
                let updateCourse = temp[i]
                updateCourse.display = !updateCourse.display;
                temp[i] = updateCourse;
                this.setState({courses: temp});
            }
        }
    }

     canDisplay = (course) => {
        if (course.display === true) {
            return (<tr key={course.name}>
                       <td>{course.name}</td>
                        <td>{course.desc}</td>
                    </tr>)
        }
     }
     getCard = (course) => {
        return (
            <Course key={course.name} name={course.name} desc={course.desc}
               details={course.details} display={course.display}
               handleUpdate={this.handleUpdate}/>
        )
     }
    render() {
        return (
            <div>
             <Card className="m-4">
                 <CardHeader>Courses</CardHeader>
                 <CardBody>
                    <Row>
                     {this.state.courses.map((course) => this.getCard(course))}
                    </Row>
                 </CardBody>
             </Card>
             <Card className="m-4">
                <CardHeader>Your Selections</CardHeader>
                 <CardBody>
                    <Table>
                        <thead>
                            <tr className="text-center">
                                <th>Course Name</th>
                                <th>Description</th>
                            </tr>
                        </thead>
                        <tbody className="text-center" >
                            {this.state.courses.map((course) => this.canDisplay(course))}
                        </tbody>
                    </Table>
                 </CardBody>
             </Card>
            </div>
        );
    }
}

export default AllCourses;
