import 'bootstrap/dist/css/bootstrap.min.css';
import {Component} from "react/cjs/react.production.min";
import React from 'react'
import {
  Card, CardHeader, CardBody, Table, Row
} from 'reactstrap';
import Course from "./Course";

class AllCourses extends Component {
    constructor() {
        super()

        this.state = {
            courses: []
        }
        this.handleUpdate = this.handleUpdate.bind(this)
    }
    updateData = (apiResponse) => {
        this.setState({courses: apiResponse})
    }

    fetchData = () => {
        //In package.json add "proxy": "http://localhost:5000"
        //This will allow redirect to REST api in Flask w/o CORS errors
         fetch('/coursedata')
         .then(
             response => response.json()
             )//The promise response is returned, then we extract the json data
         .then (jsonOutput => //jsonOutput now has result of the data extraction
                  {
                      this.updateData(jsonOutput)
                    }
              )
      }
    componentDidMount(){
        this.fetchData();
    }



    handleUpdate = (course) => {
        for(let i= 0; i < this.state.courses.length; i++) {
            if (this.state.courses[i].name === course.props.name) {
                let temp = this.state.courses
                let updateCourse = temp[i]
                updateCourse.display = !updateCourse.display;
                temp[i] = updateCourse;
                this.setState({courses: temp});
            }
        }
    }

     canDisplay = (course) => {
        if (course.display === true) {
            return (<tr key={course.name}>
                       <td>{course.name}</td>
                        <td>{course.desc}</td>
                    </tr>)
        }
     }
     getCard = (course) => {
        return (
            <Course key={course.name} name={course.name} desc={course.desc}
               details={course.details} display={course.display}
               handleUpdate={this.handleUpdate}/>
        )
     }
    render() {
        return (

            <div>
             <Card className="m-4">
                 <CardHeader>Courses</CardHeader>

                 <CardBody>
                    <Row>
                     {this.state.courses.map((course) => this.getCard())}
                        {console.log(this.state.courses)}
                    </Row>
                 </CardBody>
             </Card>
             <Card className="m-4">
                <CardHeader>Your Selections</CardHeader>
                 <CardBody>
                    <Table>
                        <thead>
                            <tr className="text-center">
                                <th>Course Name</th>
                                <th>Description</th>
                            </tr>
                        </thead>
                        <tbody className="text-center" >
                            {this.state.courses.map((course) => this.canDisplay(course))}
                        </tbody>
                    </Table>
                 </CardBody>
             </Card>
            </div>
        );
    }
}

export default AllCourses;
